package com.sunwenjiu.second.message.service;import com.sunwenjiu.second.message.entity.Message;import com.sunwenjiu.second.shiro2.vo.ResultVO;import org.springframework.data.domain.Page;import java.util.List;import java.util.Map;/** * 消息业务层 * @author SWJ * */public interface MessageService {    /**     * 创建消息，     * @param userIds  接收人集合     * @param initiatorId 发送人id     * @param title 标题     * @param message 消息内容     */    void createMessage(List<String> userIds, String initiatorId, String title, String content);    /**     * 查询未读消息的条数     * @param userId 需要查询的UserID     */    long messageUnreadCount(String userId);    /**     * 把一条消息标记为已读     * @param msgId 消息id     */    Integer messageHasRead(String msgId);    /**     * 通过消息ID返回该消息的内容     * @param msgId 消息id     * @return 消息的内容     */    String getMessageContentById(String msgId);    /**     * 初始化该用户的所有消息     * @param userId 该用户     * @return  Map集合  包含：unread  readed     */    Map<String,List> getMessageList(String userId);    /**     * 分页查询     * @param pageNo     * @param pageSize     * @return     */    Page<Message> findAllPage(int pageNo, int pageSize);    /**     * 通过id批量删除数据     * @param ids     */    void deleteByMsgIdIn(List<String> ids);    /**     * 获取不同消息类型，不同条数的消息     * @param messageType     * @param limit     * @return     */    List<Message> getListByType(int messageType, String receiveId, int limit);}